import React from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';

import Search from "./Pages/Search";
import Article from './Pages/Article'
import Navbar from './Component/Navbar'
import NotFound from './Component/NotFound'


class App extends React.Component {

	constructor( props ) {
		super( props );

		this.state = {
			query: '',
												results: {},
												loading: false,
												message: '',
		};
	}

	fetchSearchResults = (updatedPageNo = '', query ) => {

		// const pageNumber = updatedPageNo ? `&page=${updatedPageNo}` : '';

		// By default the limit of results is 20
		const searchUrl = `https://nutritionix-api.p.rapidapi.com/v1_1/search/${query}`;
		const options = {
			method: 'GET',
			url: searchUrl,
			params: {fields: 'item_name,item_id,brand_name,nf_calories,nf_total_fat'},
			headers: {
				'x-rapidapi-key': '518dacdf68mshe074609f26cc4e9p107185jsn84ce96ca1395',
				'x-rapidapi-host': 'nutritionix-api.p.rapidapi.com'
			}
		};

		if (this.cancel) {
			// Cancel the previous request before making a new request
			this.cancel.cancel();
		}
		// Create a new CancelToken
		this.cancel = axios.CancelToken.source();

		trackPromise(axios.request(options, {
				cancelToken: this.cancel.token,
			})
			.then((res) => {
				const resultNotFoundMsg = !res.data.hits.length
					? 'Makanan Tidak Terdaftar.'
					: '';

				this.setState({
					results: res.data.hits,
					message: resultNotFoundMsg,
					loading: false,
				});
			})

			.catch((error) => {
				if (axios.isCancel(error) || error) {
					this.setState({
						loading: false,
						message: 'Internet Tidak Terdeteksi.',
					});
				}
			}));
	};

	handleOnInputChange = (event) => {
		const query = event.target.value;
			if ( !query ) {
				this.setState({ query, results: {}, message: '' } );
			} else {
				this.setState({ query, loading: true, message: '' }, () => {
					this.fetchSearchResults(1, query);
				});
			}
		console.log(this.state.results)
	};

	renderSearchResults = () => {
		const {results} = this.state;

		if (Object.keys(results).length && results.length) {
			return (
					<div className="hasil-search">
							<div className="row">
							{results.map((result,i) => {
								return (
									<SearchCard
										id={results[i].fields._id}
										item={results[i].fields.item_name}
										brand={results[i].fields.brand_name}
										porsi={results[i].fields.nf_serving_size_qty}
										kalori={results[i].fields.nf_calories}
										lemak={results[i].fields.nf_total_fat}
									/>
									);
								})}
						</div>
				</div>


			);
		}
	};

	render() {
		return (
			<div>
				<Navbar/>
				<Switch>
					<Route path="/search" exact component={ Search } />
					<Route path="/article" exact component={ Article } />
					<Route component={NotFound}/>
				</Switch>
			</div>
		);
	}
}

export default App;
