				this.setState({
					thumbnailResults: res.data.image_results
					// articleMessage: resultNotFoundMsg,
				});
				// console.log(this.state.thumbnailResults[1].image.src)
			})

			.catch((error) => {
					console.log(error)
			}));

	}

async getGoogleNews(){

