import React from 'react';
import './Search.css';
import axios from 'axios';
import { Card,ListGroup,ListGroupItem } from 'react-bootstrap';

class Search extends  React.Component {

	constructor( props ) {
		super( props );

		this.state = {
			query: '',
                        results: {},
                        loading: false,
                        message: '',
		};
	}

	fetchSearchResults = (updatedPageNo = '', query ) => {

		// const pageNumber = updatedPageNo ? `&page=${updatedPageNo}` : '';

		// By default the limit of results is 20
		const searchUrl = `https://nutritionix-api.p.rapidapi.com/v1_1/search/${query}`;
		const options = {
		  method: 'GET',
		  url: searchUrl,
		  params: {fields: 'item_name,item_id,brand_name,nf_calories,nf_total_fat'},
		  headers: {
		    'x-rapidapi-key': '518dacdf68mshe074609f26cc4e9p107185jsn84ce96ca1395',
		    'x-rapidapi-host': 'nutritionix-api.p.rapidapi.com'
		  }
		};

		if (this.cancel) {
			// Cancel the previous request before making a new request
			this.cancel.cancel();
		}
		// Create a new CancelToken
		this.cancel = axios.CancelToken.source();

		axios.request(options, {
				cancelToken: this.cancel.token,
			})
			.then((res) => {
				const resultNotFoundMsg = !res.data.hits.length
					? 'There are no more search results. Please try a new search.'
					: '';

				this.setState({
					results: res.data.hits,
					message: resultNotFoundMsg,
					loading: false,
				});
			})

			.catch((error) => {
				if (axios.isCancel(error) || error) {
					this.setState({
						loading: false,
						message: 'Failed to fetch results.Please check network',
					});
				}
			});
	};

	handleOnInputChange = (event) => {
		const query = event.target.value;

		if ( ! query ) {
			this.setState({ query, results: {}, message: '' } );
		} else {
			this.setState({ query, loading: true, message: '' }, () => {
				this.fetchSearchResults(1, query);
			});
		}
		console.log(this.state.results)
	};


	renderSearchResults = () => {
		const {results} = this.state;

		if (Object.keys(results).length && results.length) {
			return (
					<div className="row">
					{results.map((result,i) => {
						return (
							<div className="col-12" key={result._id}>
								<div className="card card-custom">
									<div className="card-body">
										<h5 className="card-title">{results[i].fields.item_name}</h5>
									</div>
									<ul className="list-group list-group-flush">
										<li className="list-group-item">Cras justo odio</li>
										<li className="list-group-item">Dapibus ac facilisis in</li>
										<li className="list-group-item">Vestibulum at eros</li>
									</ul>
								</div>
							</div>
							);
						})}
				</div>


			);
		}
	};

	render() {
		const {query} = this.state.query;
		return (
			<div className="container">
				{/*Heading*/}
				<h2>Cari Makanan</h2>
					<div className="col-12">
							<input
							 className="form-control me-2"
							 type="text"
							 name="query"
							 value={query}
							 placeholder="Search..."
							 onChange={this.handleOnInputChange}
							/>
					</div>
				{this.renderSearchResults()}
			</div>
			)
	}
}

export default Search;
